mapper.select:
SELECT
FROM awardclassify ORDER BY sort ASC
LIMIT #{startno},#{pagesize}

mapper.insert:
insert into awardclassify (id, classifyname, parentid,
  sort, classifytype, updatetime, createtime
  )
values (#{id,jdbcType=INTEGER}, #{classifyname,jdbcType=VARCHAR}, #{parentid,jdbcType=INTEGER},
  #{sort,jdbcType=INTEGER}, #{classifytype,jdbcType=INTEGER}, #{updatetime,jdbcType=TIMESTAMP}, #{createtime,jdbcType=TIMESTAMP}
  )
