mapper.update:
update classroom_courses
set isdefault = 0 
where classroomid = #{classroomid}

update classroom_courses
set isdefault = #{isdefault} 
where id = #{id}

update classroom_courses
set isdel = 1 
where id = #{id} 
and classroomid = #{classroomid}

update classroom_courses
set coursesort = #{coursesort}  
where id = #{id} 
and classroomid = #{classroomid}

update classroom_courses
set isup = #{isup}  
where id = #{id} 
and classroomid = #{classroomid}

update classroom_courses
set isup = #{isup},
liveid = #{liveid}   
where id = #{id} 
and classroomid = #{classroomid}

update classroom_courses
set coursetype = #{coursetype}  
where classroomid = #{classroomid}

update classroom_courses
set coursetype = #{coursetype}  
where id = #{id} 
and classroomid = #{classroomid}

update classroom_courses
set status = #{status}
where id = #{id}
and classroomid = #{classroomid}
