mapper.select:
select 

from classroom_courses
where id = #{id} 
and classroomid = #{classroomid}

select
from classroom_courses where classroomid = #{classroomid} and isdel = 0 and pickey != '' order by coursesort desc, createtime desc
limit #{startNum},#{endNum}

select
from classroom_courses where classroomid = #{classroomid} and isdel = 0 and pickey != '' order by createtime desc
limit #{startNum},#{endNum}

SELECT count(*) from classroom_courses 
where classroomid = #{classroomid} 
and pickey != ''

mapper.update:
update classroom_courses
set isdefault = 0 
where classroomid = #{classroomid}

update classroom_courses
set isdefault = #{isdefault} 
where id = #{id}

update classroom_courses
set isdel = 1 
where id = #{id} 
and classroomid = #{classroomid}

update classroom_courses
set coursesort = #{coursesort}  
where id = #{id} 
and classroomid = #{classroomid}
