select 

from classroom_courses 
where classroomid = #{classroomid} 
and isdel = 0 
and pickey != '' 

  	and isup = #{isup}

order by coursesort asc, createtime desc 

  limit #{startNum},#{endNum}

select
from classroom_courses where classroomid = #{classroomid} and isdel = 0 and pickey != '' and isup = 1 and teachingtypes = 1 and (status = 0 or status = 1) order by createtime desc
limit #{startNum},#{endNum}

select count(*) 
from classroom_courses
