mapper.select:
select 

from classroom
where isdel = 0

  and ptype = #{ptype}


	and ispublic = #{ispublic}

order by createtime desc 

  limit #{startNum},#{endNum}

select 

from classroom
where
isdel = 0

  and userid = #{userid}


	and ptype = #{ptype} 

order by createtime desc 

  limit #{startNum},#{endNum}

select
from classroom where isdel = 0
AND (classtitle LIKE CONCAT(CONCAT('%', #{keyword}),'%'))
and ptype = #{ptype}
order by createtime desc
limit #{startNum},#{endNum}

mapper.update:
update classroom
set 
  classnotice = #{classnotice} 
where classroomid = #{classroomid}

update classroom
set 
  classinvoloed = classinvoloed + #{num} 
where classroomid = #{classroomid}

update classroom
set 

    charge = #{charge},


    freecoursenum = #{freecoursenum},

  isfree = #{isfree} 
where classroomid = #{classroomid}
