mapper.select:
select 

from classroom_members
where classroomid = #{classroomid} 
and itype = 0 
order by createtime desc 

  limit #{startNum},#{endNum}

select 

from classroom_members
where userid = #{userid} 
and itype = 0 
order by createtime desc 

  limit #{startNum},#{endNum}

select 

from classroom_members
where classroomid = #{classroomid} 
and userid = #{userid} 

	and itype = #{itype}

mapper.update:
update classroom_members
set itype = #{itype} 
where classroomid = #{classroomid} 
and userid = #{userid}
