mapper.select:
select 

from classroom_members
where classroomid = #{classroomid} 
and itype = 0 
order by createtime desc 

  limit #{startNum},#{endNum}

select 

from classroom_members
where userid = #{userid} 
and itype = 0 
order by createtime desc 

  limit #{startNum},#{endNum}

select 

from classroom_members
where classroomid = #{classroomid} 
and userid = #{userid} 

	and itype = #{itype}

mapper.update:
update classroom_members
set itype = #{itype} 
where classroomid = #{classroomid} 
and userid = #{userid}

update classroom_members
set icount = icount + #{icount} 
where classroomid = #{classroomid} 
and userid = #{userid}

update classroom_members
set complaintotalcount = complaintotalcount + #{complaintotalcount} 
where classroomid = #{classroomid} 
and userid = #{userid}

update classroom_members


    flowers = flowers + #{flowers},


    likes = likes + #{likes},

  userid = #{userid} 

where 

  likes > ROUND(#{likes}) and


  flowers > ROUND(#{flowers}) and

classroomid = #{classroomid} 
and userid = #{userid}
