mapper.select:
select

from imp_all_detail
WHERE userid = #{userid}

  AND impid=#{impid}


  AND gtype=#{gtype}


  AND detailtype=#{detailtype}

ORDER BY createtime DESC

mapper.delete:
DELETE FROM imp_all_detail
WHERE userid = #{userid}

  AND impid=#{impid}


  AND gtype=#{gtype}


  AND detailtype=#{detailtype}
