mapper.select:
select

from imp_all_detail
WHERE
   impid=#{impid}
  AND detailtype=#{detailtype}

    AND createtime < #{lastdate}

ORDER BY createtime DESC
  limit #{pagesize}

SELECT userid FROM imp_all_detail
WHERE
userid = #{userid}
AND  impid=#{impid}
AND  gtype=#{gtype}
AND  detailtype=#{detailtype}
limit 0,1

mapper.delete:
DELETE FROM imp_all_detail
WHERE userid = #{userid}

  AND impid=#{impid}


  AND gtype=#{gtype}


  AND detailtype=#{detailtype}
