mapper.select:
select 

from imp_complaints 
where 1=1 

  and status != 0 


  and status = 0 


  and username = #{username} 


  and businesstype = #{businesstype} 


  AND dealtime >= #{sdealtime} 


  AND dealtime < #{edealtime} 

order by createtime desc 

	LIMIT #{pageNo},#{pageSize}

	SELECT count(*) from imp_complaints 
	where 1=1 

    and status != 0 


    and status = 0 


    and username = #{username} 


    and businesstype = #{businesstype} 


    AND dealtime >= #{sdealtime} 


    AND dealtime < #{edealtime}

	SELECT count(*) from imp_complaints 
	where 1=1 

    and status != 0 


    and status = 0 


    and impid = #{impid} 


    and businesstype = #{businesstype} 


    AND businessid = #{businessid}

mapper.update:
update imp_complaints
set 
  dealtime = #{dealtime},

    dealuser = #{dealuser},


    checkoption = #{checkoption},

  status = #{status} 
where 1=1 

  and impid = #{impid} 


  and businessid = #{businessid} 


  and businesstype = #{businesstype}
