mapper.select:
select

from ${tablename}

where impid = #{impid,jdbcType=BIGINT}

  and isdel = #{isdel,jdbcType=CHAR}


  and ispublic = #{ispublic,jdbcType=CHAR}

mapper.update:
UPDATE ${tablename}
SET flowers = flowers + #{flowernum}
WHERE

  businessid = #{businessid} AND

impid = #{impid}

UPDATE ${tablename}
SET diamonds = diamonds + #{diamondnum}
WHERE

  businessid = #{businessid} AND

AND  impid = #{impid}

UPDATE ${tablename}
SET

pickey = #{pickey},


  filekey = #{filekey}

WHERE
  sourcekey = #{key}

    AND
    businessid = #{businessid}

mapper.select:
select #{orderby} a, 






from ${tablename}


    ismainimp = #{ismainimp}


    AND isresponded = 0


    AND userid = #{userid}

  and businessid = #{businessid}
  and isdel = '0'


    ORDER BY createtime DESC


    ORDER BY sum(flowers*10+likes) desc

limit #{startno},#{pagesize}

SELECT ir.id, ir.impid, ir.itype, ir.brief, ir.pickey, ir.filekey, ir.userid, ir.businessid, ir.businesstype, ir.likes,rm.likes as totallikes,rm.flowers as totalflowers, ir.flowers, ir.diamonds, ir.createtime, ir.updatetime, ir.indexnum, ir.isdel, ismainimp, ir.ptype, ir.ispublic,ir.sourcekey,ir.complaincount,rm.sortnum FROM improve_rank ir JOIN rank_members rm ON ir.businessid=rm.rankid and rm.userid = ir.userid
rm.status = '1' and ir.isdel = 0 AND ir.businessid = #{businessid}
AND ir.createtime < #{lastdate}
AND ismainimp = '1' group by ir.userid
ORDER BY ir.createtime DESC
ORDER BY ir.flowers*#{flowerscore}+ir.likes*#{likescore} desc
limit #{pagesize}
limit #{startno},#{pagesize}

SELECT count(*) FROM improve_goal ig JOIN user_goal ug ON ig.businessid=ug.goalid WHERE ug.ispublic='2' AND ug.isdel = '0' AND ig.isrecommend='0'
AND ig.createtime >= #{startdate}
AND ig.brief LIKE concat(concat('%',#{brief}),'%')
AND ig.userid IN
#{item.id}

SELECT ig.id, ig.impid, ig.itype, ig.brief, ig.pickey, ig.filekey, ig.userid, ig.businessid, ig.businesstype, ig.likes, ig.flowers, ig.diamonds, ig.createtime, ig.updatetime, ig.indexnum, ig.isdel, ig.ptype, ig.ispublic,ig.sourcekey,ig.complaincount FROM improve_goal ig JOIN user_goal ug ON ig.businessid=ug.goalid WHERE ug.ispublic='2' AND ug.isdel = '0' AND ig.isrecommend='0'
AND ig.createtime >= #{startdate}
AND ig.brief LIKE concat(concat('%',#{brief}),'%')
AND ig.userid IN
#{item.id}
ORDER BY ig.createtime DESC
ORDER BY ig.likes DESC ,ig.createtime DESC
ORDER BY ig.flowers DESC ,ig.createtime DESC
limit #{startno},#{pagesize}

SELECT count(DISTINCT (ir.id)) FROM improve_rank ir JOIN rank_members rm ON ir.businessid=rm.rankid WHERE rm.status = '1' AND ir.status = '0' AND ir.isrecommend='0'
AND ir.createtime >= #{startdate}
AND ir.brief LIKE concat(concat('%',#{brief}),'%')
AND ir.userid IN
#{item.id}

SELECT ir.id, ir.impid, ir.itype, ir.brief, ir.pickey, ir.filekey, ir.userid, ir.businessid, ir.businesstype, ir.likes,rm.likes as totallikes,rm.flowers as totalflowers, ir.flowers, ir.diamonds, ir.createtime, ir.updatetime, ir.indexnum, ir.isdel, ismainimp, ir.ptype, ir.ispublic,ir.sourcekey,ir.complaincount,rm.sortnum FROM improve_rank ir JOIN rank_members rm ON ir.businessid=rm.rankid WHERE rm.status = '1' AND ir.status = '0' AND ir.isrecommend='0'
AND ir.createtime >= #{startdate}
AND ir.brief LIKE concat(concat('%',#{brief}),'%')
AND ir.userid IN
#{item.id}
GROUP BY ir.id
ORDER BY ir.createtime DESC
ORDER BY ir.likes DESC ,ir.createtime DESC
ORDER BY ir.flowers DESC ,ir.createtime DESC
limit #{startno},#{pagesize}

SELECT count(*) FROM improve WHERE ispublic = '0' AND isdel = '0' AND isrecommend='0'
AND createtime >= #{startdate}
AND brief LIKE concat(concat('%',#{brief}),'%')
AND userid IN
#{item.id}

