mapper.update:
UPDATE improve_goal
set isdel = '1'
where userid=#{userid}
and businessid=#{businessid}
and businesstype=#{businesstype}

UPDATE improve_goal
set ismainimp = '0'
where ismainimp = '1'
and userid=#{userid}
and businessid=#{businessid}

UPDATE improve_rank
set ismainimp = '0'
where ismainimp = '1'
and userid=#{userid}
and businessid=#{businessid}

UPDATE ${tablename} set ismainimp='1'
where ${field}=#{businessid}
and userid=#{userid}
AND isdel='0'
order by updatetime desc limit 1

UPDATE ${tablename}
set likes=likes-#{like},
    flowers=flowers-#{flower},
    icount = icount-1
where ${field}=#{businessid}
and userid=#{userid}

update ${tablename}
SET 
    likes = likes + #{icount}


      flowers = flowers + #{icount}

where ${field}=#{businessid}
and userid=#{userid}

UPDATE ${tablename}
SET isrecommend=#{isrecommend}
WHERE impid in

  #{item}

UPDATE ${tablename}
SET isdel = '1'
WHERE businessid=#{businessid} and impid=#{impid}
