mapper.select:
select

from ${tablename}

where impid = #{impid,jdbcType=BIGINT}

  and isdel = #{isdel,jdbcType=CHAR}


  and ispublic = #{ispublic,jdbcType=CHAR}

mapper.update:
UPDATE ${tablename} SET

  likes = likes + 1


  likes = likes - 1

WHERE
businessid = #{businessid}
AND impid=#{impid}

UPDATE ${tablename}
SET flowers = flowers + #{flowernum}
WHERE
businessid = #{businessid}
AND  impid = #{impid}

UPDATE ${tablename}
SET diamonds = diamonds + #{diamondnum}
WHERE
businessid = #{businessid}
AND  impid = #{impid}

UPDATE ${tablename}
SET

pickey = #{pickey},


  filekey = #{filekey}

WHERE
  sourcekey = #{key}

    AND
    businessid = #{businessid}

mapper.select:
select

from ${tablename}
where
 businessid = #{businessid}

  AND ismainimp = #{ismainimp}

ORDER BY createtime DESC
limit #{startno},#{pagesize}
