mapper.select:
select

from ${tablename}

where impid = #{impid,jdbcType=BIGINT}

  and isdel = #{isdel,jdbcType=CHAR}


  and ispublic = #{ispublic,jdbcType=CHAR}

mapper.update:
UPDATE ${tablename}
SET flowers = flowers + #{flowernum}
WHERE

  businessid = #{businessid} AND

impid = #{impid}

UPDATE ${tablename}
SET diamonds = diamonds + #{diamondnum}
WHERE

  businessid = #{businessid} AND

AND  impid = #{impid}

UPDATE ${tablename}
SET

pickey = #{pickey},


  filekey = #{filekey}

WHERE
  sourcekey = #{key}

    AND
    businessid = #{businessid}

mapper.select:
select #{orderby} a, 






from ${tablename}
where
 businessid = #{businessid}

  AND ismainimp = #{ismainimp}


  AND isresponded = 0 


    ORDER BY createtime DESC


    ORDER BY sum(flowers*10+likes) desc

limit #{startno},#{pagesize}

select

from improve_circle
