    AND createdate < #{ecreatedate}


    AND awardid IN

SELECT



    AND rankid = #{rankid}


    AND receivecode = #{receivecode}


    AND username = #{username}


    AND ranktitle = #{ranktitle}


    AND awardnickname = #{awardnickname}


    AND awardcateid = #{awardcateid}


    AND status = #{status}


  AND createdate >= #{screatedate}


  AND createdate < #{ecreatedate}


