mapper.select:
SELECT
  COUNT(1)
FROM rank
WHERE 1=1

  AND ranktitle LIKE concat(concat('%',#{ranktitle}),'%')


  AND rankbrief LIKE concat(concat('%',#{rankbrief}),'%')


  AND ptype = #{ptype}


  AND ranktype = #{ranktype}


  AND isfinish = #{isfinish}


  AND starttime >= #{sstarttime}


  AND starttime < #{estarttime}


  AND rankscope = #{rankscope}


  AND isup = #{isup}


  AND isdel = #{isdel}


  AND sourcetype = #{sourcetype}


  AND createuserid = #{createuserid}


  AND ishomerecommend = #{ishomerecommend}


  AND isrecommend = #{isrecommend}

SELECT

FROM rank
WHERE 1=1

  AND ranktitle LIKE concat(concat('%',#{rank.ranktitle}),'%')


  AND rankbrief LIKE concat(concat('%',#{rank.rankbrief}),'%')


  AND ptype = #{rank.ptype}


  AND ranktype = #{rank.ranktype}


  AND isfinish = #{rank.isfinish}


  AND starttime >= #{rank.sstarttime}


  AND starttime < #{rank.estarttime}


  AND rankscope = #{rank.rankscope}


  AND isup = #{rank.isup}


  AND isdel = #{rank.isdel}


  AND sourcetype = #{rank.sourcetype}


  AND createuserid = #{rank.createuserid}


  AND ishomerecommend = #{rank.ishomerecommend}


  AND isrecommend = #{rank.isrecommend}



    ORDER BY weight DESC ,createtime DESC


    ORDER BY createtime DESC




  limit #{startno},#{pagesize}

mapper.update:
update rank
set rankinvolved = rankinvolved + #{count,jdbcType=INTEGER}
where

    rankinvolved > 1

  rankid = #{rankId,jdbcType=BIGINT}

mapper.select:
SELECT

FROM rank
WHERE 1=1

    AND (ranktitle LIKE concat(concat('%',#{ranktitle}),'%')
    OR codeword = #{codeword})


    AND rankbrief LIKE concat(concat('%',#{rankbrief}),'%')


      and id > #{lastRankId}


      and endtime > #{minEndDate}


    AND ptype = #{ptype}


    AND starttime >= #{starttime}


    AND starttime < #{starttime}


    AND rankscope = #{rankscope}


    AND isup = #{isup}


    AND isdel = #{isdel}


    and isfinish = #{isfinish}


    and isfinish > 1


    AND ispublic = #{ispublic}


    AND sourcetype = #{sourcetype}


    AND createuserid = #{createuserid}



      and isrecommend = #{isrecommend}


      order by endtime DESC


    order by weight DESC


    order by starttime


    order by starttime DESC


      order by id desc



    limit #{startNum},#{pageSize}


      limit 0,#{pageSize}

select

from rank
where starttime > #{beforeDate}
and starttime <= #{currentDate}
and isup = 1
and isfinish = 1

mapper.update:
update rank
set isfinish = 1
where isup = 1 AND starttime > #{beforeDate}
    and starttime <= #{currentDate}

mapper.select:
select

from rank
where isup = 1 and endTime <= #{currentTime}
and isfinish = #{isfinish}
