mapper.select:
select count(*) from rank_members where
rankid=#{rankid} AND status = '1'

  AND isfashionman = #{isfashionman}




      AND (checkstatus = '2' OR checkstatus = '3')


      AND (checkstatus != '0')


      AND checkstatus = #{checkstatus}




  AND  userid in

    #{item.id}

SELECT

FROM rank_members
WHERE
rankid=#{rankmember.rankid} AND status = '1'

  AND isfashionman = #{rankmember.isfashionman}




      AND (checkstatus = '2' OR checkstatus = '3')


      AND (checkstatus != '0')


      AND checkstatus = #{rankmember.checkstatus}




  AND  userid in

    #{item.id}


ORDER BY sortnum DESC ,likes DESC

  limit #{startno},#{pagesize}

SELECT *
  FROM
(SELECT  FROM rank_members
WHERE rankid=#{rankmember.rankid} AND status = '1' AND checkstatus = '0' limit #{totalcount}) temp
WHERE 1=1

  AND temp.isfashionman = #{rankmember.isfashionman}


  AND  temp.userid in

    #{item.id}


ORDER BY temp.sortnum DESC ,temp.likes DESC

  limit #{startno},#{pagesize}

select

from
    rank_members
where
    rankid = #{rankid}
  and userid = #{userid}

mapper.update:
update
  rank_members
set
  rankinvolved = rankinvolved + #{count}
where rankid = #{rankId}

update rank_members
status = #{status},
icount =0, likes =0, flowers =0,
sortnum = #{sortNum},
checkstatus = #{checkstatus}
where userid = #{userId} and rankid = #{rankId}

UPDATE rank_members
  set icount = icount+#{icount}
where rankid=#{rankid}
and userid=#{userid}
