mapper.select:
select count(*) from rank_members where
rankid=#{rankid} AND status = '1'

  AND isfashionman = #{isfashionman}




      AND (checkstatus = '2' OR checkstatus = '3')


      AND checkstatus = #{checkstatus}




  AND  userid in

    #{item.id}

SELECT

FROM rank_members
WHERE
rankid=#{rankmember.rankid} AND status = '1'

  AND isfashionman = #{rankmember.isfashionman}




      AND (checkstatus = '2' OR checkstatus = '3')


      AND checkstatus = #{rankmember.checkstatus}




  AND  userid in

    #{item.id}


ORDER BY sortnum DESC ,likes DESC

  limit #{startno},#{pagesize}

mapper.update:
UPDATE rank_members

  set icount = icount+1


  set icount = icount-1

where rankid=#{rankid}
and userid=#{userid}
