mapper.select:
select count(*) from rank_members where
rankid=#{rankid} AND status = '1'

  AND isfashionman = #{isfashionman}


  AND  userid in

    #{item.id}

SELECT

FROM rank_members
WHERE
rankid=#{rankmember.rankid} AND status = '1'

  AND isfashionman = #{rankmember.isfashionman}


  AND userid = #{rankmember.userid}


  AND  userid in

    #{item.id}


ORDER BY sortnum DESC ,likes DESC

  limit #{startno},#{pagesize}
