mapper.select:
select

from
  sns_group
where
  groupid =#{groupId}

     and mainuserid = #{userId}

mapper.update:
update sns_group


    groupname = #{groupName},


    updatetime = #{updateDate},


    needconfirm = #{needConfirm},


    notice = #{notice}


where groupId = #{groupId}

mapper.select:
select

FROM sns_group
where groupid = #{keyword}
limit #{startNum},#{pageSize}
