mapper.select:
select
from user_address where userid = #{userid} and isdel = '0' order by isdefault desc
LIMIT #{pageNo},#{pageSize}

mapper.update:
update user_address 
set isdefault = #{isdefault,jdbcType=CHAR} 
where id = #{id,jdbcType=INTEGER}

update user_address 
set isdel = '1' 
where userid = #{userid} and id = #{id}
