mapper.select:
SELECT count(*) FROM user_info


    AND nickname LIKE concat(concat("%",#{nickname}),"%")


    AND username LIKE concat(concat("%",#{username}),"%")


    AND isfashionman = #{isfashionman}


    AND sex = #{sex}


    AND grade >= #{sgrade}


    AND grade < #{egrade}


    AND createtime >= #{screatetime}


    AND createtime < #{ecreatetime}

select userid, avatar, username, nickname from user_info where (nickname LIKE concat(concat('%', #{nickname}),'%') or username LIKE concat(concat('%', #{nickname}),'%'))
and userid in (${ids})
limit #{startNum},#{endNum}

mapper.update:
update user_info
deviceindex = #{deviceindex}, devicetype = #{devicetype},
where username = #{username}

update user_info
set point = #{point,jdbcType=INTEGER},
    curpoint = #{curpoint,jdbcType=INTEGER},
    grade = #{grade,jdbcType=INTEGER}
WHERE userid = #{userid,jdbcType=BIGINT}

update user_info
set totalcoin = #{totalcoin} 
WHERE userid = #{userid}

update user_info
set totalmoney = #{totalmoney} 
WHERE userid = #{userid}

update
    user_info
set totalcoin = totalcoin + #{coin}
where

    totalcoin > ROUND(#{coin}) and

  userid = #{userid}

update user_info
set
  defaultbg=#{bg}
WHERE
  userid = #{userid}
