mapper.insert:
insert into user_interests (ptype, userid, createtime
)
values (#{ptype,jdbcType=VARCHAR}, #{userid,jdbcType=VARCHAR}, #{createtime,jdbcType=TIMESTAMP}
  )

mapper.update:
update user_interests

    ptype = #{ptype,jdbcType=VARCHAR},
    updatetime = #{updatetime,jdbcType=TIMESTAMP}

where userid = #{userid}
