  #{department,jdbcType=VARCHAR}, #{location,jdbcType=VARCHAR}, #{starttime}, 
  #{endtime,jdbcType=TIMESTAMP}, #{createtime,jdbcType=TIMESTAMP}, #{updatetime,jdbcType=TIMESTAMP}
  )
