mapper.delete:
delete from user_msg 
where userid = #{userid} 

	and mtype = #{mtype} 


	and msgtype = #{msgtype}

delete from user_msg
where id = #{id} 
and userid = #{userid}

delete from user_msg
where userid = #{userid} 
and mtype = #{mtype}

delete from user_msg
where userid = #{userid} 
and mtype = 1 
and msgtype = #{msgtype}

mapper.insert:
insert into user_msg (userid, friendid,
  msgtype, snsid, remark, 
  isdel, isread, gtype, mtype, num, createtime,updatetime
  )
values ( #{userid,jdbcType=BIGINT}, #{friendid,jdbcType=BIGINT},
  #{msgtype,jdbcType=CHAR}, #{snsid,jdbcType=BIGINT}, #{remark,jdbcType=VARCHAR}, 
  #{isdel,jdbcType=CHAR}, #{isread,jdbcType=CHAR}, #{gtype,jdbcType=CHAR}, #{mtype,jdbcType=CHAR}, 
  #{num,jdbcType=INTEGER}, #{createtime,jdbcType=TIMESTAMP},#{createtime,jdbcType=TIMESTAMP}
  )

mapper.update:
update user_msg
SET  isread = 1,updatetime = now()
where id = #{id} 
and userid = #{userid}

update user_msg
set isread = 1,updatetime = now()
where userid = #{userid}

	and mtype = #{mtype} 


	and msgtype = #{msgtype}

mapper.select:
select count(1)
from user_msg
WHERE userid = #{userId}
    and msgtype = #{msgType}
    and gtype = #{gType}
    and snsid = #{snsId}

mapper.update:
