mapper.select:
select 

from user_msg
where userid = #{userid} 
and mtype = 0 
order by createtime desc 

  limit #{startNum},#{endNum}

mapper.delete:
delete from user_msg
where userid = #{userid} 
and mtype = #{mtype}

mapper.insert:
insert into user_msg (id, userid, friendid, 
  msgtype, snsid, remark, 
  isdel, isread, gtype, mtype, createtime
  )
values (#{id,jdbcType=INTEGER}, #{userid,jdbcType=BIGINT}, #{friendid,jdbcType=BIGINT}, 
  #{msgtype,jdbcType=CHAR}, #{snsid,jdbcType=BIGINT}, #{remark,jdbcType=VARCHAR}, 
  #{isdel,jdbcType=CHAR}, #{isread,jdbcType=CHAR}, #{gtype,jdbcType=CHAR}, #{mtype,jdbcType=CHAR}, 
  #{createtime,jdbcType=TIMESTAMP}
  )

mapper.update:
update user_msg
  isread = 1 
where userid = #{userid} 
and mtype = #{mtype}
