mapper.select:
select 

from user_msg
where userid = #{userid} 
and mtype = 0 
order by createtime desc

  limit #{startNum},#{endNum}

select
from user_msg where userid = #{userid} and mtype = 1 and msgtype not in (2,5) order by createtime desc
limit #{startNum},#{endNum}

select 

from user_msg
where userid = #{userid} 
and mtype = 1 
and msgtype = #{msgtype}  
order by createtime desc

  limit #{startNum},#{endNum}

