mapper.delete:
delete from user_msg 
where mtype = 1 
and msgtype = 1 

	and snsid = #{snsid}  


	and gtype = #{gtype} 


	and gtypeid = #{gtypeid} 


	and commentid = #{commentid} 


	and commentlowerid = #{commentlowerid}

delete from user_msg 
where userid = #{userid} 

	and mtype = #{mtype} 


	and msgtype = #{msgtype}

delete from user_msg
where id = #{id} 
and userid = #{userid}

delete from user_msg
where userid = #{userid} 
and mtype = #{mtype}

delete from user_msg
where userid = #{userid} 
and mtype = 1 
and msgtype = #{msgtype}

mapper.update:
update user_msg
SET  isread = 1,updatetime = now()
where id = #{id} 
and userid = #{userid}

update user_msg
set isread = 1,updatetime = now()
where userid = #{userid}

	and mtype = #{mtype} 


	and msgtype = #{msgtype}

mapper.select:
select count(1)
from user_msg
WHERE userid = #{userId}
    and msgtype = #{msgType}
    and gtype = #{gType}
    and gtypeid = #{snsId}

mapper.update:
update user_msg
set isdel = 0,isread = 0,updatetime = now()

    ,remark = #{remark}

where userid = #{userId}
    and msgtype = #{msgType}
    and gtype = #{gType}
    and gtypeid = #{snsId}

