mapper.select:
SELECT

FROM user_pl_detail
where userid=#{userid} and ptype != 'a' and toplevel = 1 ORDER BY toplevel,ptype limit #{startNum},#{pageSize}

SELECT
FROM user_pl_detail where userid=#{userid} and ptype != 'a' and toplevel = 0
and ptype not in
#{item}
ORDER BY toplevel,ptype
limit #{startNum},#{pageSize}

mapper.update:
UPDATE user_pl_detail


    toplevel = #{toplevel,jdbcType=CHAR},


where userid = #{userid,jdbcType=BIGINT} and ptype=#{ptype,jdbcType=CHAR}

update user_pl_detail
set
  ptype = #{ptype,jdbcType=CHAR},
  toplevel = #{toplevel,jdbcType=CHAR},
  updatetime = #{updatetime,jdbcType=TIMESTAMP}
where userid = #{userid,jdbcType=BIGINT} and ptype = #{ptype,jdbcType=CHAR}
